                'type' => $trip['type']
            ]);
            $driver = Driver::updateOrCreate([
                'driver_pic' => $trip['driver_pic'],
                'driver_name' => $trip['driver_name']
            ]);
            $vehicle = Vehicle::updateOrCreate([
                'car_make' => $trip['car_make'],
                'car_model' => $trip['car_model'],
                'car_number' => $trip['car_number'],
                'car_year' => $trip['car_year']
            ], [
                'car_pic' => $trip['car_pic'],
            ]);
            DriverVehicle::updateOrCreate([
                'driver_id' => $driver->id,
                'vehicle_id' => $vehicle->id
            ]);
            $t = Trip::updateOrCreate([
                'trip_type_id' => $trip_type->id,
                'vehicle_id' => $vehicle->id,
                'driver_id' => $driver->id,
                'request_date' => $trip['request_date'],
                'pickup_lat' => $trip['pickup_lat'],
                'pickup_lng' => $trip['pickup_lng'],
                'pickup_location' => $trip['pickup_location'],
                'pickup_date' => $trip['pickup_date'],
                'dropoff_lat' => $trip['dropoff_lat'],
                'dropoff_lng' => $trip['dropoff_lng'],
                'dropoff_location' => $trip['dropoff_location'],
                'dropoff_date' => $trip['dropoff_date'],
                'driver_rating' => $trip['driver_rating'],
                'status' => $trip['status']
            ]);
            TripDetail::updateOrCreate([
                'trip_id' => $t->id,
                'duration' => $trip['duration'],
                'duration_unit' => $trip['duration_unit'],
                'distance' => $trip['distance'],
                'distance_unit' => $trip['distance_unit'],
                'cost' => $trip['cost'],
                'cost_unit' => $trip['cost_unit']
            ]);
